import { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

// Custom Components
import Sidebar from './components/Sidebar/Sidebar';

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import CustomForm from './components/Form/Form';



// Guide
import bg from './img/app-bg.png';
var containerStyle = {
  backgroundImage: `url(${bg})`,
  backgroundSize: `cover`,
  height: `100vh`,
}

    // Values of form
    const [values, setValues] = useState({
      projectName: '',
      projectOwner: '',
  });


// Form functions
    // Handler for inputs
    const handleProjectName = (event) => {
      event.persist();
      setValues((values) => ({
          ...values,
          projectName: event.target.value,
      }));
  };

  const handleProjectOwner = (event) => {
      event.persist();
      setValues((values) => ({
          ...values,
          projectOwner: event.target.value,
      }));
  };

const App = () => {
  const [sidebarState, setSidebar] = useState(false);
  const [dashState, setDashState] = useState(false);

  const openSidebar = (event) => {
    return setSidebar(true);
  };

  const closeSidebar = (event) => {
    setDashState(true);
    setSidebar(false);
  };  

  return (
    <div style={containerStyle}>
      <Container>
        <Button onClick={openSidebar}/>

        {sidebarState ? 
        <div className="w-25 p-3 bg-light">
          <CustomForm />
          <Button onClick={closeSidebar}>Submit</Button>
        </div>
        : null}

        {dashState ? 
          <CustomerDash projectName={values.projectName} projectOwner={values.projectOwner}/>
        : null}

        </Container>    
    </div>
  );
}

export default App;
