import { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

// Custom Components
import Sidebar from './components/Sidebar/Sidebar';

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import CustomForm from './components/Form/Form';
import CustomerDash from './components/CustomerDash/CustomerDash';



// Guide
import bg from './img/app-bg.png';
var containerStyle = {
  backgroundImage: `url(${bg})`,
  backgroundSize: `cover`,
  height: `100vh`,
}

var buttonStyle = { 
  boxSizing: `border-box`,
  position: `relative`,
  outline: `none`,
  padding: `0px`,
  margin: `0px`,
  border: `none`,
  backgroundColor: `rgb(44, 160, 28)`,
  borderRadius: `18px`,
  cursor: `pointer`,
  fill: `rgb(255, 255, 255)`,
  minHeight: `36px`,
  minWidth: `100px`,
}

const App = () => {
  const [sidebarState, setSidebar] = useState(false);
  const [dashState, setDashState] = useState(false);
  const [projectName, setName] = useState("projectName: ''");


  function handleChange(newName, newOwner) {
    setName(newName);
  }

  const openSidebar = (event) => {
    setSidebar(true);
  };

  const closeSidebar = (event) => {
    setDashState(true);
    setSidebar(false);
  };  

  return (
    <div style={containerStyle}>
      <Container>

        <div class="idsZeroState_content"><h1 class="idsZeroState_content_header">Run your projects with confidence</h1><p class="idsZeroState_content_subhead">Make better decisions by knowing how your jobs are doing</p><div class="idsZeroState_content_layout"><ha-video videokey="ha-player555" tabindex="-1" class="ha-video-no-history-view" hiderelatedvideos="true" id="basic-video" url="https://www.youtube.com/embed/xyjDK4VUo0Q?autoplay=0&amp;rel=0">
    <div class="ha-video-history-view ha-video-history-view-hidden hidden">
        <h3 tabindex="-1" class="ha-video-history-title">Recently Watched</h3>
        <div class="ha-video-links"></div>
        <div class="ha-video-history-edit">
            <button class="ha-video-history-edit-button no-button-style ha-video-focus-button">Edit</button>
        </div>
        <div class="ha-video-history-controls hidden">
            <button id="ha-video-clear-all-button555" class="no-button-style ha-video-focus-button">Clear All</button>
            <button class="ha-video-done-editing-button no-button-style ha-video-focus-button">Done</button>
        </div>
    </div>
    <div class="ha-video-main-div">
        <div class="ha-video-title-bar">
            <div class="ha-video-history-option"><button aria-label="Toggle History View" class="hi hi-list no-button-style hidden"></button></div>
            <h3>How to set up and use projects in QuickBooks Online</h3>
            <div class="ha-video-size-options">
                <button id="ha-video-restore555" aria-label="Return to full size view" class="hi hi-vidresize-1 no-button-style hidden"></button>
                <button id="ha-video-minimize555" aria-label="Switch to compact view" class="hi hi-vidresize-2 no-button-style"></button>
                <button aria-label="Close video player" class="hi hi-close no-button-style"></button>
            </div>
        </div>
        <iframe id="ha-player555" frameborder="0" allowfullscreen="1" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" title="Video" width="640" height="360" src="https://www.youtube.com/embed/xyjDK4VUo0Q?enablejsapi=1&amp;showinfo=0&amp;modestbranding=1&amp;autoplay=0&amp;rel=0&amp;origin=https%3A%2F%2Fapp.qbo.intuit.com&amp;widgetid=1" tabindex="0"></iframe>
        <div class="ha-video-footer-bar"></div>
   </div>
    <div tabindex="0" aria-hidden="true"></div>
    <section class="hidden"></section>

</ha-video><div class="video-link"><i class="hi page large hi-play video-icon"></i>See how it works</div><div class="list-layout"><div class="benefits-container"><ul><li class="list-workflow"><span class="list-item-title">Profitability in one place</span><span class="list-item-subtext">Organize your project finances with a clear view of profits</span></li><li class="list-workflow"><span class="list-item-title">Keep track of your labor costs</span><span class="list-item-subtext">See where your team is spending time and how it affects your profit margins</span></li><li class="list-workflow"><span class="list-item-title">Eliminate the guesswork</span><span class="list-item-subtext">Understand which projects make money and where you should focus your efforts</span></li></ul></div><div class="button-layout"><button class="StyledButton__Wrapper-vnaxcc-1 gYvrJh"><span class="StyledButton__Overlay-vnaxcc-0 ljmCd"><span class="StyledButtonLabel__Label-ri94b7-0 icsGej">Start a project</span></span></button></div></div><div class="tips-section"><div class="tip-label"><span>TIPS &amp; RESOURCES</span></div><div class="links"><a class="tip-link" tabindex="0" target="_blank" href="https://quickbooks.intuit.com/community/Help-Articles/Track-income-costs-and-profitability-by-project/m-p/249111">Track income, costs, and profitability by project</a><a class="tip-link" tabindex="0" target="_blank" href="https://quickbooks.intuit.com/community/Help-Articles/Get-your-project-labor-costs-in-real-time/m-p/249136">Get your project labor costs in real-time</a><a class="tip-link" tabindex="0" target="_blank" href="https://quickbooks.intuit.com/community/Help-Articles/What-are-pay-rates-cost-rates-and-billable-rates/m-p/249137">What are pay rates, cost rates, and billable rates?</a><a class="tip-link" tabindex="0"><span>See more</span></a></div></div></div></div>
        <Button style={buttonStyle} onClick={openSidebar}>Start new project</Button>

        {sidebarState ? 
        <div className="w-25 p-3 bg-light">
          <CustomForm onChange={handleChange}/>
          <Button style={buttonStyle} onClick={closeSidebar}>Submit</Button>
        </div>
        : null}

        {dashState ? 
          <CustomerDash projectName={projectName}/>
        : null}

        </Container>    
    </div>
  );
}

export default App;
