import { Component, useState } from "react";
import styles from './Form.module.css';

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import Dropdown from 'react-bootstrap/Dropdown';
import FormControl from 'react-bootstrap/FormControl';

const CustomForm = (props) => {
    const [newCustState, setCustState] = useState(false);

    function handleChange(event) {
        props.onChange(event.target.value);
    }

    const handleNewUser = () => {
        setCustState(true);
    };

    const saveNewUser = () => {
        setCustState(false);
    };

    return (
        <div className={styles.drawerpanel}>
            <Form>
                <Form.Group>
                    <Form.Label>Project Name</Form.Label>
                    <Form.Control 
                        value={props.value} 
                        onChange={handleChange}
                        placeholder="What's the project name?" 
                    />
                </Form.Group>

                <InputGroup>
                    <FormControl placeholder="What's your name?"/>
                    <Dropdown.Item as="button" onChange={handleChange} onClick={handleNewUser} value={props.value} ></Dropdown.Item>
                </InputGroup>

                {newCustState ? 
                <Form.Group>
                    <Form.Label>New Customer</Form.Label>
                    <Form.Control 
                        value={props.value} onChange={handleChange}
                    />
                    <Button onClick={saveNewUser}>Save</Button>
                </Form.Group>
                : null}
            </Form>
        </div>
    );
};
export default CustomForm;
