import ReactDOM from "react-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

// Custom Components
import Sidebar from './components/Sidebar/Sidebar';

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import CustomForm from './components/Form/Form';
import CustomerDash from './components/CustomerDash/CustomerDash';
import ContextContainer from './components/State';

// Guide
import bg from './img/app-bg.png';
var containerStyle = {
  backgroundImage: `url(${bg})`,
  backgroundSize: `cover`,
  height: `100vh`,
}

const initialAppState = {
  name: "",
  owner: "",
};

const App = () => {
  const [sidebarState, setSidebar] = useState(false);
  const [dashState, setDashState] = useState(false);
  const [appState, updateAppState] = useState(initialAppState);
  // const [name, setName] = useState("projectName: ");
  // const [owner, setOwner] = useState("projectOwner: ");

  const openSidebar = (event) => {
    setSidebar(true);
  };

  const closeSidebar = (event) => {
    setDashState(true);
    setSidebar(false);
  };  

  return (
    <div style={containerStyle}>
      <Container>
        <Button onClick={openSidebar}/>

        {sidebarState ? 
        <div className="w-25 p-3 bg-light">
            <CustomForm />
          <Button onClick={closeSidebar}>Submit</Button>
        </div>
        : null}

        {dashState ? 
          <ContextContainer.Provider value={initialAppState}>
            <CustomerDash 
              projectName={name.props}
              projectOwner={initialAppState.owner}
            /> 
          </ContextContainer.Provider>
        : null}

        </Container>    
    </div>
  );
}

export default App;
