import { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

// Custom Components
import Sidebar from './components/Sidebar/Sidebar';

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import CustomForm from './components/Form/Form';
import CustomerDash from './components/CustomerDash/CustomerDash';



// Guide
import bg from './img/app-bg.png';
var containerStyle = {
  backgroundImage: `url(${bg})`,
  backgroundSize: `cover`,
  height: `100vh`,
}

const App = () => {
  const [sidebarState, setSidebar] = useState(false);
  const [dashState, setDashState] = useState(false);
  const [values, setValues] = useState({
      projectName: '',
      projectOwner: '',
  });

  function handleChange(newValue) {
    setValues(newValue);
  }

  // // Handler for inputs
  // const handleProjectName = (event) => {
  //     setValues((values) => ({
  //         projectName: event.target.value,
  //     }));
  // };

  // const handleProjectOwner = (event) => {
  //     setValues((values) => ({
  //         projectOwner: event.target.value,
  //     }));
  // };

  const openSidebar = (event) => {
    setSidebar(true);
  };

  const closeSidebar = (event) => {
    setDashState(true);
    setSidebar(false);
  };  

  return (
    <div style={containerStyle}>
      <Container>
        <Button onClick={openSidebar}/>

        {sidebarState ? 
        <div className="w-25 p-3 bg-light">
          <CustomForm values={values} onChange={handleChange}/>
          <Button onClick={closeSidebar}>Submit</Button>
        </div>
        : null}

        {dashState ? 
          <CustomerDash values={values.projectName}/>
        : null}

        </Container>    
    </div>
  );
}

export default App;
