import { useEffect, useState, createContext } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

// Custom Components
import Sidebar from './components/Sidebar/Sidebar';

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import CustomForm from './components/Form/Form';
import CustomerDash from './components/CustomerDash/CustomerDash';



// Guide
import bg from './img/app-bg.png';
var containerStyle = {
  backgroundImage: `url(${bg})`,
  backgroundSize: `cover`,
  height: `100vh`,
}

const ContextContainer = createContext(null);

const initialAppState = {
  projectName: "",
  projectOwner: "",
};

const App = () => {
  const [sidebarState, setSidebar] = useState(false);
  const [dashState, setDashState] = useState(false);
  const [appState, updateAppState] = useState(initialAppState);
  // const [name, setName] = useState("projectName: ");
  // const [owner, setOwner] = useState("projectOwner: ");

  // function handleChange(newName) {
  //   setName(newName);
  // }

  // function handleChange2(newOwner) {
  //   setOwner(newOwner);
  // }


  // // Handler for inputs
  // const handleProjectName = (event) => {
  //     setValues((values) => ({
  //         projectName: event.target.value,
  //     }));
  // };

  // const handleProjectOwner = (event) => {
  //     setValues((values) => ({
  //         projectOwner: event.target.value,
  //     }));
  // };

  const openSidebar = (event) => {
    setSidebar(true);
  };

  const closeSidebar = (event) => {
    setDashState(true);
    setSidebar(false);
  };  

  return (
    <div style={containerStyle}>
      <Container>
        <Button onClick={openSidebar}/>

        {sidebarState ? 
        <div className="w-25 p-3 bg-light">
          <ContextContainer.Provider value={{ appState, updateAppState }}>
            <CustomForm />
          </ContextContainer.Provider>
          <Button onClick={closeSidebar}>Submit</Button>
        </div>
        : null}

        {dashState ? 
          <CustomerDash projectName={appState.projectName}/> 
        : null}

        </Container>    
    </div>
  );
}

export default App;
