import { Component, useState } from "react";

// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import FormControl from 'react-bootstrap/FormControl';
import CustomerDash from '../CustomerDash/CustomerDash';

const Sidebar = ({useToggle}) => {

    // Values of form
    const [values, setValues] = useState({
        projectName: '',
        projectOwner: '',
    });

    const [isOn, toggleIsOn] = useToggle();

    // Handler for inputs
    const handleProjectName = (event) => {
        event.persist();
        setValues((values) => ({
            ...values,
            projectName: event.target.value,
        }));
    };

    const handleProjectOwner = (event) => {
        event.persist();
        setValues((values) => ({
            ...values,
            projectOwner: event.target.value,
        }));
    };

    // Close sidebar
    // let closeSidebar = () => {
    //     return setSidebar(!showSidebar)
    // };

    return (
        <div className="w-25 p-3 bg-light">
            {isOn ?
            <Form>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Project Name</Form.Label>
                    <Form.Control 
                        value={values.projectName} 
                        onChange={handleProjectName}
                        placeholder="What's the project name?" 
                    />
                </Form.Group>

                <InputGroup>
                    <FormControl
                        value={values.projectOwner} 
                        onChange={handleProjectOwner}
                        placeholder="What's your name?"
                    />

                    {/* <DropdownButton
                        as={InputGroup.Append}
                        variant="outline-secondary"
                        id="input-group-dropdown-2"
                        >
                        <Dropdown.Item href="#">Action</Dropdown.Item>
                        <Dropdown.Item href="#">Another action</Dropdown.Item>
                        <Dropdown.Item href="#">Something else here</Dropdown.Item>
                        <Dropdown.Divider />
                        <Dropdown.Item href="#">Separated link</Dropdown.Item>
                    </DropdownButton> */}
                </InputGroup>
            </Form>
            : null}

            <Button onClick={toggleIsOn}/>

            <CustomerDash projectName={values.projectName} projectOwner={values.projectOwner}/>
        </div>
    );
};
export default Sidebar;
