import { Component, useState } from "react";
import styles from './Form.module.css';


// Bootstrap Components
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import FormControl from 'react-bootstrap/FormControl';

const CustomForm = (props) => {

    // Values of form
    const [values, setValues] = useState({
        projectName: '',
        projectOwner: '',
    });

    const [newCustState, setCustState] = useState(false);

    // Handler for inputs
    const handleProjectName = (event) => {
        event.persist();
        setValues((values) => ({
            ...values,
            projectName: event.target.value,
        }));
    };

    const handleProjectOwner = (event) => {
        event.persist();
        setValues((values) => ({
            ...values,
            projectOwner: event.target.value,
        }));
    };

    const handleNewUser = (event) => {
        setCustState(true);
    };

    const saveNewUser = (event) => {
        setCustState(false);
    };

    return (
        <div>
            <Form>
                <Form.Group>
                    <Form.Label>Project Name</Form.Label>
                    <Form.Control 
                        value={values.projectName} 
                        onChange={handleProjectName}
                        placeholder="What's the project name?" 
                    />
                </Form.Group>

                <InputGroup>
                    <FormControl
                        value={values.projectOwner} 
                        onChange={handleProjectOwner}
                        placeholder="What's your name?"
                    />
                    <Dropdown.Item onClick={handleNewUser} className={styles.inputBG} >Add {values.projectOwner}</Dropdown.Item>
                </InputGroup>

                {newCustState ? 
                <Form.Group>
                    <Form.Label>New Customer</Form.Label>
                    <Form.Control 
                        value={values.projectOwner} 
                    />
                    <Button onClick={saveNewUser}>Save</Button>
                </Form.Group>
                : null}
            </Form>
        </div>
    );
};
export default CustomForm;
